const BACK_TO_TOP = "BACK_TO_TOP";
const SEND_EMAIL = "SEND_EMAIL";
const WINDOW_WIDTH = "WINDOW_WIDTH";
const OPEN_MOBILE = "OPEN_MOBILE"

//////////Contexts//////////
const BackToTopContext = createContext();
//const SendContactFormContext = createContext();
