//////////Reducer//////////
const AppReducer = (state, action) => {
    switch (action.type) {
        case BACK_TO_TOP:
            return {
                ...state,
                backToTop: action.payload,
            }
        case SEND_EMAIL:
            return {
                ...state,
                ...action.payload
            }
        case WINDOW_WIDTH:
            return {
                ...state,
                isMobile: action.payload,
            }
        case OPEN_MOBILE:
            return {
                ...state,
                openMobile: action.payload,
            }
        default:
            return state;
    }
}


