//////////Layout mounter//////////
function Site(props) {
    const backToTopContext = useContext(BackToTopContext);
    // const windowWidthContext = useContext(WindowWidthContext);
    // const {openMobile} = windowWidthContext;

    return (
        <>
            <Transition in={backToTopContext.state.backToTop} timeout={300}>
                {state => (<ArrowUp transitions={`arrow-${state}`} />)}
            </Transition>
            <Transition in={backToTopContext.state.backToTop} timeout={200}>
                {state => (<NavBar transitions={`navbar-${state}`} />)}
            </Transition>
            <Social />
            <Header />
            <Exp />
            <Projects />
            <Education />
            <About />
            <ContactArea />
            <ContentContainer>
                <ViewportExp />
                <Exp />
                <ViewportProjects />
                <Projects />
                <ViewportEducation />
                <Education />
                <ViewportAbout />
                <About />
                <ViewportContact />
                <ContactArea />
            </ContentContainer>
            <ContentContainer>
                <ViewportExp />
                <ViewportProjects />
                <ViewportEducation />
                <ViewportAbout />
                <ViewportContact/>
            </ContentContainer>
            <Footer />
        </>
    )
}

////////App & Contexts//////////
function App() {
    return (
        <BackToTopState>
            <Site />
        </BackToTopState>
    )
}

